package marko_manev;

public class Einsteig05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Arrays
			// Container für gleiche Datentypen, dargestellt durch []
			// Rückblick zu Python: Liste
			// Dort waren alles Datentypen (String, int, double,...) gleichzeitig möglich
	 
			int[] numbers;
			// [ <erster Wert> | <zweiter Wert> | ...]
			numbers = new int[5];
			// beim Anlegen muss die Größe des Array festgelegt werden
			// i.d.R. ist sie nicht mehr zu ändern
			// Am Anfang sind nur defaultwerte (hier 0) vergeben
			// keine eigene Initialisierung bislang
			
			numbers[0]=1;
			numbers[1]=2;
			numbers[2]=3;
			numbers[3]=4;
			numbers[4]=5;
			int[] numbers2 = { 1, 2, 3, 4, 5 };
			
			// es geht auch Größenzuweisung und Initialisierung gleichzeitig
			
			for (int i = 0; i < numbers.length; i++) {
				System.out.println(numbers[i]);
			}
	 
			for (int i = 0; i < numbers2.length; i++) {
				System.out.println(numbers2[i]);
			}
	 
			for (int i = 0; i < numbers.length; i++) {
				numbers[i] = i;
			}
	 
			for (int i = 0; i < numbers.length; i++) {
				System.out.println(numbers[i]);
			
			
			
			
			
			
			
			}}}
			
				
	 
			
			
		
	 
	


